INSERT INTO `shopping_cart_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('1782', '1439', 'pjField', '3', 'title', 'Add space type name and description. You can set different number of available parking spaces for this type for different periods during the year. For example you can create a space type with 100 spaces for 1st Jan - 31 May, and then during the summer season 1st June - 31th Aug you can set different amount of available spaces for this same type.', 'script');
