INSERT INTO `shopping_cart_multi_lang` (`id`, `foreign_id`, `model`, `locale`, `field`, `content`, `source`) VALUES('1791', '1442', 'pjField', '3', 'title', 'Add different extras which people can purchase along with the parking space. For each extra you can add name, description and price. Price can be for the whole booking or per day. Each extra also has type - Limited and Unlimited. If extra is limited then there is a fixed number of items that can be booked. Under Count textbox you can set the number of available items. ''Is single'' option is used to specify if users should select number of extras that can be purchased.', 'script');
